import Content from './components/content'

const Course = ({ courses }) => {
  return (
    <div>
      <h1>{courses.name}</h1>
      <Content parts={course.sparts} />
    </div>
  )
}

const App = () => {
  const courses = [
    {
      name: 'Half Stack application development',
      id: 1,
      parts: [
        {
          name: 'Fundamentals of React',
          exercises: 10,
          id: 1
        },
        {
          name: 'Using props to pass data',
          exercises: 7,
          id: 2
        },
        {
          name: 'State of a component',
          exercises: 14,
          id: 3
        },
        {
          name: 'Redux',
          exercises: 11,
          id: 4
        }
      ]
    }, 
    {
      name: 'Node.js',
      id: 2,
      parts: [
        {
          name: 'Routing',
          exercises: 3,
          id: 1
        },
        {
          name: 'Middlewares',
          exercises: 7,
          id: 2
        }
      ]
    }
  ]

  const total = courses.parts.reduce((total, currentValue) => {
    return total + currentValue.exercises
  }, 0)

  return (
    <div>
    <Course key={courses.id} courses={courses} />
    <b>total of {total} exercises</b>
    </div>
  )

}

export default App
